gpointer bot_ref (gpointer instance) {
	Bot* self;
	self = instance;
	g_atomic_int_inc (&self->ref_count);
	return instance;
}


void bot_unref (gpointer instance) {
	Bot* self;
	self = instance;
	if (g_atomic_int_dec_and_test (&self->ref_count)) {
		BOT_GET_CLASS (self)->finalize (self);
		g_type_free_instance ((GTypeInstance *) self);
	}
}


